  function fetchData() {
        fetch("http://api.openweathermap.org/data/2.5/weather?q=${search}&appid=cf54e950b60c2deed59ee5ec5142964b").then(Response => {
                if (!Response.ok )
                throw console.error('NOOOOOO');
                console.log(Response);
     return Response.json()
     
        })
        .then(data =>{
         console.log(data.main.temp);
         let celcius =Math.round(data.main.temp - 273.15)

         document.querySelector('#temperature').innerHTML = `<td>${celcius}</td>`
         var input = document.getElementById("myInput");
         let search = input.value
        })
        .catch(error => {
            console.log(error);    
        });
       
  }
 

var button = document.querySelector("btn");
button.addEventListener("click" , function () {
  fetchData()
})
  

  


